define(function(require,exports,module){"use strict";var dom=require("../../lib/dom");var cssText=require("../../requirejs/text!./settings_menu.css");dom.importCssString(cssText);module.exports.overlayPage=function overlayPage(editor,contentElement,top,right,bottom,left){top=top?"top: "+top+";":"";bottom=bottom?"bottom: "+bottom+";":"";right=right?"right: "+right+";":"";left=left?"left: "+left+";":"";var closer=document.createElement("div");var contentContainer=document.createElement("div");function documentEscListener(e){if(e.keyCode===27){closer.click()}}closer.style.cssText="margin: 0; padding: 0; "+"position: fixed; top:0; bottom:0; left:0; right:0;"+"z-index: 9990; "+"background-color: rgba(0, 0, 0, 0.3);";closer.addEventListener("click",function(){document.removeEventListener("keydown",documentEscListener);closer.parentNode.removeChild(closer);editor.focus();closer=null});document.addEventListener("keydown",documentEscListener);contentContainer.style.cssText=top+right+bottom+left;contentContainer.addEventListener("click",function(e){e.stopPropagation()});var wrapper=dom.createElement("div");wrapper.style.position="relative";var closeButton=dom.createElement("div");closeButton.className="ace_closeButton";closeButton.addEventListener("click",function(){closer.click()});wrapper.appendChild(closeButton);contentContainer.appendChild(wrapper);contentContainer.appendChild(contentElement);closer.appendChild(contentContainer);document.body.appendChild(closer);editor.blur()}});